import requests

url = "https://google-play-store10.p.rapidapi.com/applications/details"
querystring = {"language": language, "country": country, "id": id}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "google-play-store10.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())
