import requests

url = "https://streamlinewatch-streaming-guide.p.rapidapi.com/shows/{id}/episodes"
querystring = {"platform": platform, "offset": offset, "region": region, "limit": limit, "sort": sort, "season": season}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "streamlinewatch-streaming-guide.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

