import requests

url = "https://ott-details.p.rapidapi.com/advancedsearch"
querystring = {"start_year": start_year, "page": page, "max_imdb": max_imdb, "type": type, "sort": sort, "min_imdb": min_imdb, "genre": genre, "language": language, "end_year": end_year}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "ott-details.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

