import requests

url = "https://sangeetlyrics.p.rapidapi.com/lyrics"
querystring = {"movie": movie, "singer": singer, "lang": lang, "limit": limit, "year": year}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "sangeetlyrics.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

