import requests

url = "https://motogp-data-api.p.rapidapi.com/champions/search"
querystring = {"Accept-Encoding": accept_encoding, "season": season, "country": country, "constructor": constructor, "class": class, "rider": rider}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "motogp-data-api.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

