import requests

url = "https://foreca-weather.p.rapidapi.com/forecast/15minutely/{location}"
querystring = {"dataset": dataset, "windunit": windunit, "tz": tz, "tempunit": tempunit, "alt": alt, "periods": periods}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "foreca-weather.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

